
def characterReplacement(s: str, k: int) -> int:
    
    matchDic = {}
    l = 0
    kDecrement = k
    matchDic[s[l]] = 0
    currentLong =1
    maxLength = currentLong
    
    if len(s)<2:
        return 1
    
    r = l+1
    
    while r < len(s):
        if maxLength == 115:
            print(l,r,kDecrement,currentLong)
            break
        if s[r] in matchDic:
            currentLong +=1
            maxLength = max(maxLength,currentLong)
            r+=1
        elif kDecrement > 0:
            currentLong +=1
            maxLength = max(maxLength,currentLong)
            r+=1
            kDecrement-=1
        else:
            l+=1
            r = l+1
            matchDic.clear()
            matchDic[s[l]] = 0
            currentLong = 1
            kDecrement = k
    
    if kDecrement>0 and currentLong==maxLength:
        maxLength = min(len(s), (kDecrement+ maxLength))
        
characterReplacement("",100)